import { Component } from "react";
import Home from "./Components/Home/Home";
import About from "./Components/About/About";
export default class App extends Component {

  state =
  {
    products: [
      {id:1 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:false},
      {id:2 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:true},
      {id:3 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:false},
      {id:4 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:false},
      {id:5 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:true},
      {id:6 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:false},
      {id:7 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:false},
      {id:8 , name:"iphone" , code:123 , price:20000, count: 1 ,onSale:false}
  ]
  }

  updateProduct = (productIndex) => {
    let myproducts = [...this.state.products]
    myproducts = myproducts[productIndex].c
    this.setState({products:myproducts})
  }

  deleteProduct = (productID) =>{
    let myproducts = [...this.state.products]
    myproducts = myproducts.filter((el)=> el.id !== productID)
    this.setState({products:myproducts})
  }
  render() 
  {
    return <>
    <div>
    <Home update={this.updateProduct} delete={this.deleteProduct} productDetails={this.state.products} />
    <About/>
    </div>
    </>


  }

}
